    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //takes full name
        String name = FullName.getText();
        
        //initialises variable for each letter
        String firstNameLetter = " ";
        String middleNameLetter = " ";
        String lastNameLetter = " ";
        
        //gets first name first letter in lowercase
        firstNameLetter = Character.toString(name.charAt(0)).toLowerCase();
        boolean middle = true;
        for (int i =0; i<name.length(); i++){
            
            //checks if arrives at a space 
            if (" ".equals(Character.toString(name.charAt(i)))){
                
                //if middle then the middle name monogram is added, else the last name letter  
                if (middle){
                    middle = false;
                    //gets the first character of the word to lowercase
                    middleNameLetter = Character.toString(name.charAt(i+1)).toLowerCase();
                }else {
                    //gets the last name first letter to uppercase
                    lastNameLetter = Character.toString(name.charAt(i+1));
                    lastNameLetter = lastNameLetter.toUpperCase();
                }
            }
        }
        //outputs in monogramorder
        MonogramOut.setText(firstNameLetter+lastNameLetter+middleNameLetter);

    }//GEN-LAST:event_jButton1ActionPerformed

